import React, { useEffect, useState } from 'react'

export default function Price(props) {
	const [cost, setCost] = useState(null)

	useEffect(() => {
		const departureDate = new Date(props.departureDate)
		const arrivalDate = new Date(props.arrivalDate)
		const guest = props.guest
		const room = props.room

		const time = departureDate.getTime() - arrivalDate.getTime()
		const day = time / (1000 * 3600 * 24)

		const newCost = day * 5000 + guest * 2000 + room * 1000

		if (newCost !== cost) {
			setCost(newCost)
			if (props.onChangePrice) {
				props.onChangePrice(newCost)
			}
		}
	}, [props.departureDate, props.arrivalDate, props.guest, props.room])

	return (
		<div>
			<p className='price_text'>Оціночна вартість: {cost}₴</p>
		</div>
	)
}
