import React, { useState, useEffect } from 'react'

function Serv() {
	const [num, setNum] = useState()
	const [data, setData] = useState()

	useEffect(() => {
		fetch('http://localhost:8080/Hotel')
			.then(res => res.json())
			.then(num => {
				setNum(num)
			})
			.catch(err => console.log(err))
	}, [])

	useEffect(() => {
		fetch('http://localhost:8080/nomers')
			.then(res => res.json())
			.then(data => {
				setData(data)
			})
			.catch(err => console.log(err))
	}, [])
	console.log(data)
	console.log(num)

	return (
		<>
			<p>{data}</p>
			<p>{num}</p>
		</>
	)
}

Serv()

// Кількість номерів у готелі
const numberOfRooms = 50

// Генерація випадкових номерів для готелю
const generateRandomRooms = () => {
	const rooms = []
	for (let i = 0; i < numberOfRooms; i++) {
		rooms.push(Math.floor(Math.random() * 1000) + 1) // Припустимо, що номери у готелі - цілі числа від 1 до 1000
	}
	return rooms
}

// Оцінювання кімнати (функція придатності)
const evaluateRoom = (roomNumber, targetRoomNumber) => {
	return Math.abs(roomNumber - targetRoomNumber) // Оцінка базується на різниці між номером і цільовим номером
}

// Генетичний алгоритм
const geneticAlgorithm = (targetRoomNumber, populationSize, generations) => {
	let population = []

	// Створення випадкової популяції
	for (let i = 0; i < populationSize; i++) {
		population.push(generateRandomRooms())
	}

	// Повторення для кількості поколінь
	for (let gen = 0; gen < generations; gen++) {
		// Оцінка кожного члена популяції
		const fitnessScores = population.map(member => {
			return member.reduce(
				(acc, roomNumber) => acc + evaluateRoom(roomNumber, targetRoomNumber),
				0
			)
		})

		// Знаходження найкращих особин
		const bestIndex = fitnessScores.indexOf(Math.min(...fitnessScores))
		const bestMember = population[bestIndex]

		// "Схрещування" (комбінація) найкращих особин
		const newPopulation = [bestMember]
		for (let i = 1; i < populationSize; i++) {
			const parentA = population[Math.floor(Math.random() * populationSize)]
			const parentB = population[Math.floor(Math.random() * populationSize)]
			const child = parentA.map((gene, index) =>
				Math.random() < 0.5 ? gene : parentB[index]
			) // Випадковий вибір генів від батьків
			newPopulation.push(child)
		}

		population = newPopulation
	}

	// Повертаємо найкращого знайденого номера
	return population.reduce(
		(acc, member) => {
			const score = member.reduce(
				(acc, roomNumber) => acc + evaluateRoom(roomNumber, targetRoomNumber),
				0
			)
			return score < acc.score ? { roomNumber: member[0], score } : acc
		},
		{ roomNumber: null, score: Infinity }
	).roomNumber
}

const HotelRoomPicker = () => {
	const [targetRoomNumber, setTargetRoomNumber] = useState(500) // Цільовий номер, який ми хочемо знайти
	const [bestRoomNumber, setBestRoomNumber] = useState(null)

	useEffect(() => {
		// Підбір найкращого номера при монтажі компонента
		const bestRoom = geneticAlgorithm(targetRoomNumber, 100, 100)
		setBestRoomNumber(bestRoom)
	}, [targetRoomNumber])

	return (
		<div>
			<h2>Генетичний відбір номерів у готелі</h2>
			<p>Цільовий номер: {targetRoomNumber}</p>
			<p>Найдешевший номер: {bestRoomNumber}</p>
		</div>
	)
}

export default HotelRoomPicker
