import React, { useState, useEffect } from 'react'

function Nomers() {
	const [data, setData] = useState([])
	useEffect(() => {
		fetch('http://localhost:8080/nomers')
			.then(res => res.json())
			.then(data => {
				setData(data)
			})
			.catch(err => console.log(err))
	}, [])

	return data
}
// Функція придатності (fitness function)
const calculateFitness = room => {
	return 1 / (room.price / room['number of seats'])
}

// Генетичний алгоритм для пошуку найкращого номера
const geneticAlgorithm = (roomData, populationSize, generations) => {
	// Створення випадкової початкової популяції
	let population = []
	for (let i = 0; i < populationSize; i++) {
		population.push(roomData[Math.floor(Math.random() * roomData.length)])
	}

	// Повторення для кожного покоління
	for (let gen = 0; gen < generations; gen++) {
		// Оцінка кожного номера у популяції
		const evaluatedPopulation = population.map(room => {
			return { room, fitness: calculateFitness(room) }
		})

		// Сортування популяції за значенням фітнес-функції (від найкращого до найгіршого)
		evaluatedPopulation.sort((a, b) => b.fitness - a.fitness)

		// Відбір та схрещування для створення нової популяції
		const selectedIndividuals = evaluatedPopulation.slice(0, populationSize / 2)
		const newPopulation = [...selectedIndividuals]

		while (newPopulation.length < populationSize) {
			const parentA =
				selectedIndividuals[
					Math.floor(Math.random() * selectedIndividuals.length)
				].room
			const parentB =
				selectedIndividuals[
					Math.floor(Math.random() * selectedIndividuals.length)
				].room
			const crossoverPoint = Math.floor(
				Math.random() * Object.keys(parentA).length
			)

			const child = {
				...parentA,
				...parentB,
			}

			// Застосування мутації
			if (Math.random() < 0.1) {
				child.price *= Math.random() < 0.5 ? 0.9 : 1.1
				child['number of seats'] += Math.random() < 0.5 ? -1 : 1
			}

			newPopulation.push(child)
		}

		population = newPopulation
	}

	// Повернення найкращого номера з кінцевої популяції
	return population.reduce(
		(best, room) => {
			const fitness = calculateFitness(room)
			return fitness > best.fitness ? { room, fitness } : best
		},
		{ room: null, fitness: -Infinity }
	).room
}

const HotelRoomPicker = () => {
	const [bestRoom, setBestRoom] = useState()
	const roomData = Nomers() // Отримання даних про номери готелю

	useEffect(() => {
		if (roomData.length > 0) {
			// Запуск генетичного алгоритму при монтажі компонента
			const bestRoom = geneticAlgorithm(roomData, 100, 10)
			setBestRoom(bestRoom)
		}
	}, [roomData])

	return (
		<div>
			<h2>Генетичний відбір номерів у готелі</h2>
			<p>Найдешевший та найбільш просторий номер:</p>
			{bestRoom && (
				<div>
					<p>Назва: {bestRoom['nomers name']}</p>
					<p>Ціна: {bestRoom.price}</p>
					<p>Кількість місць: {bestRoom['number of seats']}</p>
					{/* Додайте інші дані про номер, які вам потрібні */}
				</div>
			)}
		</div>
	)
}

export default HotelRoomPicker
